const todayStr = await Memento.system.getCustomDate({format: 'iso'});
const today = new Date(todayStr);
today.setHours(0, 0, 0, 0);
const tomorrow = new Date(today);
tomorrow.setDate(tomorrow.getDate() + 1);

const events = await Memento.plugins.calendar.getEvents({
  startDate: today.toISOString(),
  endDate: tomorrow.toISOString()
});

const result = {
  count: events.length,
  events: events.map(e => ({
    id: e.id,
    title: e.title,
    startTime: e.startTime,
    endTime: e.endTime
  }))
};
setResult(result);
return result;

const nowStr = await Memento.system.getCustomDate({format: 'iso'});
const now = new Date(nowStr);
const weekStart = new Date(now);
weekStart.setDate(now.getDate() - now.getDay());
weekStart.setHours(0, 0, 0, 0);

const weekEnd = new Date(weekStart);
weekEnd.setDate(weekStart.getDate() + 7);

const events = await Memento.plugins.calendar.getEvents({
  startDate: weekStart.toISOString(),
  endDate: weekEnd.toISOString()
});

const result = {
  count: events.length,
  events: events.map(e => ({
    id: e.id,
    title: e.title,
    date: e.startTime
  }))
};
setResult(result);
return result;

const todayStr = await Memento.system.getCustomDate({format: 'iso'});
const tomorrow = new Date(todayStr);
tomorrow.setDate(tomorrow.getDate() + 1);
tomorrow.setHours(14, 0, 0, 0);

const endTime = new Date(tomorrow);
endTime.setHours(15, 0, 0, 0);

const event = await Memento.plugins.calendar.createEvent({
  title: '项目会议',
  startTime: tomorrow.toISOString(),
  endTime: endTime.toISOString()
});

const result = {
  success: true,
  event: {
    id: event.id,
    title: event.title,
    startTime: event.startTime
  }
};
setResult(result);
return result;
