const contacts = await Memento.plugins.contact.getContacts();

const result = {
  count: contacts.length,
  contacts: contacts.map(c => ({
    id: c.id,
    name: c.name,
    phone: c.phone,
    email: c.email
  }))
};
setResult(result);
return result;

const contact = await Memento.plugins.contact.createContact({
  name: '张三',
  phone: '13800138000',
  email: 'zhangsan@example.com'
});

const result = {
  success: true,
  contact: {
    id: contact.id,
    name: contact.name
  }
};
setResult(result);
return result;

const contacts = await Memento.plugins.contact.getContacts();
const contact = contacts[0];

if (!contact) {
  const errorResult = { success: false, message: '没有联系人' };
  setResult(errorResult);
  return errorResult;
}

const interaction = await Memento.plugins.contact.addInteraction({
  contactId: contact.id,
  type: '聚餐',
  note: '今天一起吃饭'
});

const result = {
  success: true,
  interaction: {
    contactName: contact.name,
    type: interaction.type,
    date: interaction.date
  }
};
setResult(result);
return result;

const recent = await Memento.plugins.contact.getRecentContacts({
  limit: 10
});

const result = {
  count: recent.length,
  contacts: recent.map(c => ({
    name: c.name,
    lastInteraction: c.lastInteraction
  }))
};
setResult(result);
return result;
