const notebooks = await Memento.plugins.nodes.getNotebooks();

const result = {
  success: true,
  data: {
    count: notebooks.length,
    notebooks: notebooks.map(n => ({
      id: n.id,
      name: n.name,
      nodeCount: n.nodeCount
    }))
  },
  timestamp: Date.now()
};

setResult(result);
return result;

const notebook = await Memento.plugins.nodes.createNotebook({
  name: '工作笔记'
});

const result = {
  success: true,
  data: {
    id: notebook.id,
    name: notebook.name
  },
  timestamp: Date.now()
};

setResult(result);
return result;

const notebooks = await Memento.plugins.nodes.getNotebooks();
const notebook = notebooks[0];

if (!notebook) {
  const errorResult = { success: false, message: '没有笔记本' };
  setResult(errorResult);
  return errorResult;
}

const tree = await Memento.plugins.nodes.getTree({
  notebookId: notebook.id
});

const result = {
  success: true,
  data: {
    notebookName: notebook.name,
    tree: tree
  },
  timestamp: Date.now()
};

setResult(result);
return result;

const notebooks = await Memento.plugins.nodes.getNotebooks();
const notebook = notebooks[0];

if (!notebook) {
  const errorResult = { success: false, message: '没有笔记本' };
  setResult(errorResult);
  return errorResult;
}

const node = await Memento.plugins.nodes.createNode({
  notebookId: notebook.id,
  nodeData: {
    title: '新节点',
    notes: '节点内容'
  }
});

const result = {
  success: true,
  data: {
    id: node.id,
    title: node.title,
    status: node.status
  },
  timestamp: Date.now()
};

setResult(result);
return result;
