const note = await Memento.plugins.notes.createNote({
  title: '每日总结',
  content: '## 今日完成\n\n## 明日计划\n\n## 遇到的问题\n',
  tags: ['日总结']
});

const result = {
  success: true,
  note: {
    id: note.id,
    title: note.title
  }
};

setResult(result);
return result;

const response = await Memento.plugins.notes.searchNotes({
  keyword: '项目'
});

const result = {
  success: true,
  data: response.data || [],
  timestamp: Date.now()
};

setResult(result);
return result;

const response = await Memento.plugins.notes.searchNotes({
  keyword: '',
  tags: ['工作']
});

const result = {
  success: true,
  data: response.data || [],
  timestamp: Date.now()
};

setResult(result);
return result;

const response = await Memento.plugins.notes.getFolders();

const result = {
  success: true,
  data: response.data || [],
  timestamp: Date.now()
};

setResult(result);
return result;

const folder = await Memento.plugins.notes.createFolder({
  name: '学习笔记'
});

const result = {
  success: true,
  folder: {
    id: folder.id,
    name: folder.name
  }
};

setResult(result);
return result;
