const balance = await Memento.plugins.store.getBalance();

const result = {
  balance: balance,
  message: '当前积分余额: ' + balance + ' 分'
};

setResult(result);
return result;

const response = await Memento.plugins.store.getProducts();

const result = {
  success: response.success,
  count: response.data.length,
  products: response.data.map(p => ({
    id: p.id,
    name: p.name,
    price: p.price,
    stock: p.stock
  })),
  timestamp: response.timestamp
};

setResult(result);
return result;

await Memento.plugins.store.addPoints({
  amount: 100,
  reason: '完成任务奖励'
});

const newBalance = await Memento.plugins.store.getBalance();

const result = {
  success: true,
  added: 100,
  newBalance: newBalance
};

setResult(result);
return result;

const history = await Memento.plugins.store.getExchangeHistory();

const result = {
  count: history.length,
  history: history.map(h => ({
    productName: h.productName,
    price: h.price,
    date: h.date
  }))
};

setResult(result);
return result;
