let allAccounts = [];
let offset = 0;
const count = 20;

while (true) {
  const result = await Memento.plugins.bill.getAccounts({offset, count});
  allAccounts = allAccounts.concat(result.data);

  if (!result.hasMore) break;
  offset += count;
}

setResult(`共加载 ${allAccounts.length} 个账户`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const bills = await Memento.plugins.bill.getBills({startDate, endDate});
setResult(`本月共 ${bills.length} 条账单`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const result = await Memento.plugins.bill.getBills({startDate, endDate, offset: 20, count: 20});
setResult(`还有更多: ${result.hasMore}`);

let allBills = [];
let offset = 0;
const count = 50;

while (true) {
  const result = await Memento.plugins.bill.getBills({offset, count});
  allBills = allBills.concat(result.data);

  if (!result.hasMore) break;
  offset += count;
}

setResult(`共加载 ${allBills.length} 条账单`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const bill = await Memento.plugins.bill.createBill({accountId: 'acc123', type: 'income', amount: 5000, category: '工资', description: '月度工资', date: today});
setResult(`账单已创建: ${bill.id}`);

const bill = await Memento.plugins.bill.createBill({
  accountId: 'acc123',
  type: 'expense',
  amount: -299,
  category: '购物',
  description: '购买书籍 - 技术书籍《代码大全》',
  id: 'bill123',
  date: '2025-01-15'
});
setResult(`账单已创建，自定义ID: ${bill.id}`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const stats = await Memento.plugins.bill.getStats({startDate, endDate});
setResult(`本月收入: ${stats.totalIncome}, 支出: ${stats.totalExpense}, 余额: ${stats.balance}`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const catStats = await Memento.plugins.bill.getCategoryStats({startDate, endDate});
setResult(JSON.stringify(catStats, null, 2));

const account = await Memento.plugins.bill.findAccountBy({field: 'id', value: 'acc123'});
if (account) {
  setResult(`找到账户: ${account.title}，余额: ${account.totalAmount}`);
} else {
  setResult('未找到该账户');
}

const accounts = await Memento.plugins.bill.getAccounts({});
const richAccounts = accounts.filter(acc => acc.totalAmount > 1000);
setResult(`找到 ${richAccounts.length} 个账户`);

const account = await Memento.plugins.bill.findAccountById({id: 'acc123'});
if (account) {
  setResult(`账户名: ${account.title}, 余额: ${account.totalAmount}`);
} else {
  setResult('账户不存在');
}

const bill = await Memento.plugins.bill.findBillBy({field: 'id', value: 'bill123'});
if (bill) {
  setResult(`账单: ${bill.title}, 金额: ${bill.amount}`);
} else {
  setResult('未找到该账单');
}

const bill = await Memento.plugins.bill.findBillById({id: 'bill123'});
if (bill) {
  setResult(`账单标题: ${bill.title}, 金额: ${bill.amount}, 分类: ${bill.category}`);
} else {
  setResult('账单不存在');
}

const bills = await Memento.plugins.bill.findBillByTitle({title: '地铁', fuzzy: true, findAll: true});
const total = bills.reduce((sum, b) => sum + b.amount, 0);
setResult(`找到 ${bills.length} 条地铁相关账单，合计: ${total}`);

const bills = await Memento.plugins.bill.findBillsByCategory({category: '餐饮'});
const total = bills.reduce((sum, b) => sum + b.amount, 0);
setResult(`餐饮账单共 ${bills.length} 条，合计: ${total}`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const bills = await Memento.plugins.bill.findBillsByCategory({category: '工资', startDate, endDate});
const total = bills.reduce((sum, b) => sum + b.amount, 0);
setResult(`本月工资: ${total}`);

const startDate = await Memento.system.getCustomDate({relativePosition: 'startOfMonth', format: 'yyyy-MM-dd'});
const endDate = await Memento.system.getCustomDate({relativePosition: 'endOfMonth', format: 'yyyy-MM-dd'});
const result = await Memento.plugins.bill.findBillsByCategory({category: '交通', startDate, endDate, offset: 0, count: 20});
setResult(`本月交通账单共 ${result.total} 条，当前返回 ${result.data.length} 条`);
