examples.code:
const result = await Memento.plugins.ui.alert('确认删除这个项目？', {
  title: '警告',
  confirmText: '删除',
  cancelText: '取消',
  showCancel: true
});
setResult(result);

const result = await Memento.plugins.ui.alert('检测到新版本，是否更新？', {
  title: '版本更新',
  confirmText: '立即更新',
  cancelText: '稍后再说',
  showCancel: true
});
setResult(result);

const result = await Memento.plugins.ui.dialog({
  title: '选择操作',
  content: '请选择要执行的操作',
  actions: [
    { text: '取消', value: 'cancel' },
    { text: '保存', value: 'save' },
    { text: '删除', value: 'delete', isDestructive: true }
  ]
});
setResult(result);

const color = await Memento.plugins.ui.dialog({
  title: '选择颜色',
  actions: [
    { text: '红色', value: 'red' },
    { text: '蓝色', value: 'blue' },
    { text: '绿色', value: 'green' }
  ]
});
setResult(color);

const confirmed = await Memento.plugins.ui.dialog({
  title: '确认操作',
  content: '此操作不可撤销，确定继续？',
  actions: [
    { text: '取消', value: false },
    { text: '确定', value: true }
  ]
});
setResult(confirmed);
