examples.code:
// 获取所有账户列表
var accounts = await Memento.bill.getAccounts();
console.log('账户列表:', accounts);
return accounts;

// 创建一笔账单记录
// 先获取账户
var accounts = await Memento.bill.getAccounts();

if (accounts.length > 0) {
  var accountId = accounts[0].id;

  // 参数: accountId, amount, category, title, date, note, tag
  var bill = await Memento.bill.createBill(
    accountId,
    -50.00,         // 金额 (负数=支出, 正数=收入)
    '餐饮',         // 分类
    '午餐',         // 标题
    '2025-11-14',   // 日期
    '和朋友聚餐',   // 备注
    '聚餐'          // 标签
  );

  console.log('创建成功:', bill);
  return bill;
} else {
  return '请先创建账户';
}

// 获取财务统计信息
// 参数: startDate, endDate, accountId(可选)
var stats = await Memento.bill.getStats(
  '2025-11-01',  // 开始日期
  '2025-11-30'   // 结束日期
);

console.log('统计信息:', stats);
// 包含: totalIncome, totalExpense, balance, billCount 等
return stats;

// 获取各分类的统计
var categoryStats = await Memento.bill.getCategoryStats(
  '2025-11-01',
  '2025-11-30'
);

console.log('分类统计:', categoryStats);
// 返回: {"餐饮": -800, "交通": -200, "工资": 5000}
return categoryStats;
