// 获取所有日历事件(含Todo任务)
var events = await Memento.calendar.getEvents();
console.log('事件列表:', events);
return events;

// 获取今天的所有事件
var todayEvents = await Memento.calendar.getTodayEvents();
console.log('今日事件:', todayEvents);
return todayEvents;

// 创建一个日历事件
// 参数: title, description, startTime, endTime, iconCodePoint, colorValue, reminderMinutes
var event = await Memento.calendar.createEvent(
  '项目会议',                     // 标题
  '讨论 Q4 计划',                // 描述
  '2025-11-15T09:00:00.000Z',   // 开始时间
  '2025-11-15T10:00:00.000Z',   // 结束时间(可选)
  0xe24d,                        // 图标 codePoint(可选)
  0xFF2196F3,                    // 颜色值(可选)
  15                             // 提前提醒(分钟, 可选)
);

console.log('创建成功:', event);
return event;

// 获取指定日期范围的事件
// 参数: startTime, endTime
var events = await Memento.calendar.getEventsByDateRange(
  '2025-11-14T00:00:00.000Z',  // 开始时间
  '2025-11-21T00:00:00.000Z'   // 结束时间
);

console.log('本周事件:', events);
return events;

// 标记事件为已完成
var events = await Memento.calendar.getTodayEvents();

if (events.length > 0) {
  var eventId = events[0].id;

  var result = await Memento.calendar.completeEvent(eventId);
  console.log('完成事件:', result);
  return result;
} else {
  return '今天没有事件';
}
