examples.code:
// 获取所有纪念日列表
var days = await Memento.day.getMemorialDays();
console.log('纪念日列表:', days);
return days;

// 创建一个纪念日
// 参数: name, date, notesJson(可选), backgroundColor(可选)
var day = await Memento.day.createMemorialDay(
  '结婚纪念日',                              // 名称
  '2025-08-20',                             // 日期 YYYY-MM-DD
  JSON.stringify(['准备惊喜', '订餐厅']),   // 笔记(可选)
  4294198070                                // 背景色(可选)
);

console.log('创建成功:', day);
return day;

// 计算距离指定日期的天数
// 参数: date (YYYY-MM-DD)
var result = await Memento.day.getDaysUntil('2025-12-31');
console.log('距离信息:', result);
// 包含: days(天数), isExpired(是否过期), isToday(是否今天)
return result;

// 获取N天内即将到来的纪念日
// 参数: withinDays(可选, 默认7天)
var upcoming = await Memento.day.getUpcomingDays(7);
console.log('即将到来:', upcoming);
// 包含: count(数量), days(纪念日列表)
return upcoming;

// 更新纪念日信息
var days = await Memento.day.getMemorialDays();

if (days.length > 0) {
  var dayId = days[0].id;

  // 参数: id, name, date, notesJson, backgroundColor (全部可选)
  var result = await Memento.day.updateMemorialDay(
    dayId,
    '新纪念日名称',  // 新名称(可选)
    null,            // 保持原日期
    null,            // 保持原笔记
    null             // 保持原颜色
  );

  console.log('更新成功:', result);
  return result;
} else {
  return '没有纪念日';
}
