examples.code:
// 显示一条简单的提示消息
flutter.toast('这是一条简单的提示');
return 'Toast 已显示';

// 短时间显示在顶部
flutter.toast('顶部提示', {
  duration: 'short',
  gravity: 'top'
});
return 'Toast 已显示在顶部';

// 长时间显示在中间
flutter.toast('中间提示', {
  duration: 'long',
  gravity: 'center'
});
return 'Toast 已显示在中间';

// 自定义时长（3秒）
flutter.toast('自定义时长 3 秒', {
  duration: 3000,
  gravity: 'bottom'
});
return 'Toast 已显示 3 秒';

// 仅显示确认按钮
var result = await flutter.alert('这是一条提示信息');
console.log('用户点击了确认');
return result;

// 带标题的提示
var result = await flutter.alert('操作已完成', {
  title: '成功'
});
console.log('Alert 结果:', result);
return result;

// 带取消按钮的确认对话框
var result = await flutter.alert('确定要删除吗？', {
  title: '警告',
  confirmText: '删除',
  cancelText: '取消',
  showCancel: true
});

console.log('Alert 结果:', result);
if (result.confirmed) {
  console.log('用户确认删除');
  flutter.toast('已删除', { duration: 'short' });
} else {
  console.log('用户取消删除');
  flutter.toast('已取消', { duration: 'short' });
}

return result;

// 自定义按钮文字
var result = await flutter.alert('检测到新版本，是否更新？', {
  title: '版本更新',
  confirmText: '立即更新',
  cancelText: '稍后再说',
  showCancel: true
});

var action = result.confirmed ? '立即更新' : '稍后再说';
console.log('更新选择:', action);
flutter.toast(action, { duration: 'short' });

return result;

// 简单选择对话框
var color = await flutter.dialog({
  title: '选择颜色',
  content: '请选择你喜欢的颜色',
  actions: [
    { text: '取消', value: null },
    { text: '红色', value: 'red' },
    { text: '蓝色', value: 'blue' },
    { text: '绿色', value: 'green' }
  ]
});

console.log('选择的颜色:', color);
if (color) {
  flutter.toast('你选择了 ' + color, { duration: 'short' });
}

return { color };

// 文件操作对话框
var action = await flutter.dialog({
  title: '文件操作',
  content: '请选择要执行的操作',
  actions: [
    { text: '取消', value: null },
    { text: '查看', value: 'view' },
    { text: '分享', value: 'share' },
    { text: '删除', value: 'delete', isDestructive: true }
  ]
});

console.log('选择的操作:', action);

switch (action) {
  case 'view':
    flutter.toast('正在查看文件', { duration: 'short' });
    break;
  case 'share':
    flutter.toast('正在分享文件', { duration: 'short' });
    break;
  case 'delete':
    var confirmed = await flutter.alert('确定删除？', {
      title: '二次确认',
      showCancel: true
    });
    if (confirmed.confirmed) {
      flutter.toast('文件已删除', { duration: 'long' });
    }
    break;
}

return { action };

// 多个选项的对话框
var priority = await flutter.dialog({
  title: '设置优先级',
  actions: [
    { text: '低', value: 'low' },
    { text: '中', value: 'medium' },
    { text: '高', value: 'high' }
  ]
});

console.log('选择的优先级:', priority);
if (priority) {
  flutter.toast('优先级设为: ' + priority, { duration: 'short' });
}

return { priority };

// 模拟表单提交流程
flutter.toast('准备提交表单...', { duration: 'short' });

// 等待一秒
await new Promise(resolve => setTimeout(resolve, 1000));

// 确认提交
var confirmSubmit = await flutter.alert('确定提交表单？', {
  title: '确认',
  showCancel: true
});

if (!confirmSubmit.confirmed) {
  flutter.toast('已取消提交', { duration: 'short' });
  return { submitted: false, reason: 'cancelled' };
}

flutter.toast('正在提交...', { duration: 'long' });

// 模拟网络请求
await new Promise(resolve => setTimeout(resolve, 2000));

// 模拟成功/失败（50%概率）
var success = Math.random() > 0.5;

if (success) {
  await flutter.alert('表单提交成功！', {
    title: '成功'
  });
  flutter.toast('操作完成', { gravity: 'top' });
  return { submitted: true, success: true };
} else {
  var retry = await flutter.alert('提交失败，是否重试？', {
    title: '错误',
    confirmText: '重试',
    cancelText: '取消',
    showCancel: true
  });

  return { 
    submitted: true, 
    success: false, 
    willRetry: retry.confirmed 
  };
}

// 步骤 1: 选择类型
var type = await flutter.dialog({
  title: '步骤 1/3',
  content: '选择账户类型',
  actions: [
    { text: '个人', value: 'personal' },
    { text: '企业', value: 'business' }
  ]
});

if (!type) {
  flutter.toast('已取消', { duration: 'short' });
  return { cancelled: true, step: 1 };
}

flutter.toast('选择了' + (type === 'personal' ? '个人' : '企业') + '账户', { duration: 'short' });

// 步骤 2: 选择计划
var plan = await flutter.dialog({
  title: '步骤 2/3',
  content: '选择套餐',
  actions: [
    { text: '返回', value: null },
    { text: '免费版', value: 'free' },
    { text: '专业版', value: 'pro' },
    { text: '企业版', value: 'enterprise' }
  ]
});

if (!plan) {
  flutter.toast('已返回', { duration: 'short' });
  return { cancelled: true, step: 2 };
}

// 步骤 3: 确认
var confirm = await flutter.alert(
  '账户类型: ' + type + '\n套餐: ' + plan + '\n\n确认创建？',
  {
    title: '步骤 3/3 - 确认',
    confirmText: '创建',
    cancelText: '取消',
    showCancel: true
  }
);

if (confirm.confirmed) {
  flutter.toast('正在创建账户...', { duration: 'long' });
  await new Promise(resolve => setTimeout(resolve, 2000));
  await flutter.alert('账户创建成功！', { title: '完成' });

  return { 
    success: true, 
    type: type, 
    plan: plan 
  };
} else {
  flutter.toast('已取消创建', { duration: 'short' });
  return { cancelled: true, step: 3 };
}
