examples.code:
// 获取所有仓库列表
var warehouses = await Memento.goods.getWarehouses();
console.log('仓库列表:', warehouses);
return warehouses;

// 获取所有物品
// 参数: warehouseId(可选, 用于筛选)
var items = await Memento.goods.getGoods();
console.log('物品列表:', items);
return items;

// 创建一个新物品
var warehouses = await Memento.goods.getWarehouses();

if (warehouses.length > 0) {
  var warehouseId = warehouses[0].id;

  // 参数: warehouseId, itemData(JSON)
  var itemData = JSON.stringify({
    title: '笔记本电脑',
    purchasePrice: 8999.0,
    tags: ['电子产品', '工作'],
    notes: '工作用笔记本'
  });

  var item = await Memento.goods.createGoodsItem(warehouseId, itemData);
  console.log('创建成功:', item);
  return item;
} else {
  return '请先创建仓库';
}

// 为物品添加使用记录
var items = await Memento.goods.getGoods();

if (items.length > 0) {
  var itemId = items[0].id;

  // 参数: itemId, date(可选), note(可选)
  var result = await Memento.goods.addUsageRecord(
    itemId,
    null,              // 日期(可选, 默认今天)
    '今天使用了电脑'   // 备注(可选)
  );

  console.log('记录成功:', result);
  return result;
} else {
  return '没有物品';
}

// 获取物品统计信息
var stats = await Memento.goods.getStatistics();
console.log('统计信息:', stats);
// 包含: totalCount, totalValue, unusedCount, warehouseCount
return stats;
