examples.code:
// 获取所有习惯列表
var habits = await Memento.habits.getHabits();
console.log('习惯列表:', habits);
return habits;

// 获取今天需要打卡的习惯
var todayHabits = await Memento.habits.getTodayHabits();
console.log('今日习惯:', todayHabits);
return todayHabits;

// 创建一个新习惯
// 先获取技能ID
var skills = await Memento.habits.getSkills();
var skillId = skills.length > 0 ? skills[0].id : null;

// 参数: title, durationMinutes, skillId, ...
var habit = await Memento.habits.createHabit(
  '每日阅读',      // 标题
  30,              // 时长(分钟)
  skillId          // 技能ID
);

console.log('创建成功:', habit);
return habit;

// 为习惯打卡
var habits = await Memento.habits.getHabits();

if (habits.length > 0) {
  var habitId = habits[0].id;

  // 参数: habitId, durationSeconds(可选), notes(可选)
  var record = await Memento.habits.checkIn(
    habitId,
    1800,           // 时长(秒), 可选
    '今天读完了一章'  // 备注, 可选
  );

  console.log('打卡成功:', record);
  return record;
} else {
  return '请先创建习惯';
}

// 获取习惯的统计信息
var habits = await Memento.habits.getHabits();

if (habits.length > 0) {
  var habitId = habits[0].id;

  // 获取统计: totalDurationMinutes, completionCount
  var stats = await Memento.habits.getStats(habitId);
  console.log('统计信息:', stats);
  return stats;
} else {
  return '没有习惯记录';
}
