// 获取所有笔记本列表
var notebooks = await Memento.nodes.getNotebooks();
console.log('笔记本列表:', notebooks);
return notebooks;

// 创建一个新笔记本
// 参数: title, iconCodePoint(可选), colorValue(可选)
var notebook = await Memento.nodes.createNotebook(
  '工作笔记本',     // 标题
  0xe873,          // 图标(可选)
  0xFF2196F3       // 颜色(可选)
);

console.log('创建成功:', notebook);
return notebook;

// 获取笔记本中的节点
var notebooks = await Memento.nodes.getNotebooks();

if (notebooks.length > 0) {
  var notebookId = notebooks[0].id;

  // 参数: notebookId, parentId(可选, 获取子节点)
  var nodes = await Memento.nodes.getNodes(notebookId);
  console.log('节点列表:', nodes);
  return nodes;
} else {
  return '请先创建笔记本';
}

// 在笔记本中创建节点
var notebooks = await Memento.nodes.getNotebooks();

if (notebooks.length > 0) {
  var notebookId = notebooks[0].id;

  // 参数: notebookId, nodeData(JSON)
  var nodeData = JSON.stringify({
    title: '新任务',
    status: 'todo',
    tags: ['重要'],
    notes: '任务详情'
  });

  var node = await Memento.nodes.createNode(notebookId, nodeData);
  console.log('创建成功:', node);
  return node;
} else {
  return '请先创建笔记本';
}

// 获取完整的节点树结构
var notebooks = await Memento.nodes.getNotebooks();

if (notebooks.length > 0) {
  var notebookId = notebooks[0].id;

  var tree = await Memento.nodes.getNodeTree(notebookId);
  console.log('节点树:', tree);
  return tree;
} else {
  return '请先创建笔记本';
}

// 将节点移动到新的父节点下
var notebooks = await Memento.nodes.getNotebooks();

if (notebooks.length > 0) {
  var notebookId = notebooks[0].id;
  var nodes = await Memento.nodes.getNodes(notebookId);

  if (nodes.length >= 2) {
    // 参数: notebookId, nodeId, newParentId
    var result = await Memento.nodes.moveNode(
      notebookId,
      nodes[0].id,
      nodes[1].id
    );

    console.log('移动成功:', result);
    return result;
  } else {
    return '需要至少2个节点';
  }
} else {
  return '请先创建笔记本';
}
