// 获取所有已配置的 AI 助手
var agents = await Memento.openai.getAgents();
console.log('助手列表:', agents);
return agents;

// 向 AI 助手发送消息
// 注意: 需要先获取助手ID
var agents = await Memento.openai.getAgents();

if (agents.length > 0) {
  var agentId = agents[0].id;

  // 参数: agentId, message, context(可选)
  var response = await Memento.openai.sendMessage(
    agentId,
    '请用一句话介绍 Flutter'
  );

  console.log('AI 回复:', response);
  return response;
} else {
  return '没有可用的助手，请先配置';
}

// 获取所有已配置的服务商
var providers = await Memento.openai.getProviders();
console.log('服务商列表:', providers);
return providers;

// 测试服务商连接是否正常
// 先获取服务商列表
var providers = await Memento.openai.getProviders();

if (providers.length > 0) {
  var providerId = providers[0].id;

  // 参数: providerId
  var result = await Memento.openai.testProvider(providerId);
  console.log('测试结果:', result);
  return result;
} else {
  return '没有配置的服务商';
}
