examples.code:
// 获取所有计时器任务
var timers = await Memento.timer.getTimers();
console.log('计时器列表:', timers);
return timers;

// 创建一个新的计时器任务
// 参数: name, duration(秒), type, group(可选)
var timer = await Memento.timer.createTimer(
  '学习时间',      // 名称
  1800,           // 时长(秒), 30分钟
  'countDown',    // 类型: countUp/countDown/stopwatch
  '学习'          // 分组(可选)
);

console.log('创建成功:', timer);
return timer;

// 启动计时器
var timers = await Memento.timer.getTimers();

if (timers.length > 0) {
  var timerId = timers[0].id;

  // 启动计时器
  var result = await Memento.timer.startTimer(timerId);
  console.log('已启动:', result);
  return result;
} else {
  return '请先创建计时器';
}

// 获取计时器的详细状态
var timers = await Memento.timer.getTimers();

if (timers.length > 0) {
  var timerId = timers[0].id;

  var status = await Memento.timer.getTimerStatus(timerId);
  console.log('状态信息:', status);
  // 包含: isRunning, elapsedSeconds, formattedTime 等
  return status;
} else {
  return '没有计时器';
}

// 获取所有已完成的计时历史
var history = await Memento.timer.getHistory();
console.log('历史记录:', history);
return history;
