examples.code:
// 获取所有任务
// 参数: status(可选), priority(可选)
var tasks = await Memento.todo.getTasks();
console.log('任务列表:', tasks);
return tasks;

// 获取今天截止的任务
var todayTasks = await Memento.todo.getTodayTasks();
console.log('今日任务:', todayTasks);
return todayTasks;

// 创建一个新任务
// 参数: title, description, startDate, dueDate, priority, tags
var task = await Memento.todo.createTask(
  '完成项目文档',                      // 标题
  '编写技术文档和用户手册',            // 描述(可选)
  '2025-11-14T09:00:00.000Z',        // 开始日期(可选)
  '2025-11-20T18:00:00.000Z',        // 截止日期(可选)
  'high',                             // 优先级: low/medium/high
  '["工作", "文档"]'                  // 标签(可选)
);

console.log('创建成功:', task);
return task;

// 先获取任务
var tasks = await Memento.todo.getTasks('todo');

if (tasks.length > 0) {
  var taskId = tasks[0].id;

  // 完成任务
  var result = await Memento.todo.completeTask(taskId);
  console.log('任务已完成:', result);
  return result;
} else {
  return '没有待办任务';
}

// 获取所有已过期的未完成任务
var overdueTasks = await Memento.todo.getOverdueTasks();
console.log('过期任务数量:', overdueTasks.length);
return overdueTasks;
