const todayDate = await Memento.system.getCustomDate({relativePosition: 'startOfDay', format: 'iso'});

const activities = await Memento.plugins.activity.getActivities({
  startDate: todayDate
});

const result = {
  count: activities.length,
  activities: activities.map(a => ({
    id: a.id,
    name: a.name,
    tags: a.tags,
    duration: a.duration,
    createdAt: a.createdAt
  }))
};

setResult(result);
return result;

const activity = await Memento.plugins.activity.createActivity({
  name: '跑步',
  duration: 30,
  tags: ['运动', '健身']
});

const result = {
  success: true,
  activity: {
    id: activity.id,
    name: activity.name,
    duration: activity.duration + '分钟',
    tags: activity.tags
  }
};

setResult(result);
return result;

const stats = await Memento.plugins.activity.getTodayStats();

const result = {
  totalActivities: stats.totalActivities,
  totalDuration: stats.totalDuration + '分钟',
  tagBreakdown: stats.tagBreakdown
};

setResult(result);
return result;

const tagGroups = await Memento.plugins.activity.getTagGroups();

const result = {
  groups: tagGroups.map(g => ({
    name: g.name,
    tags: g.tags,
    color: g.color
  }))
};

setResult(result);
return result;
