const accounts = await Memento.plugins.bill.getAccounts();

return {
  count: accounts.length,
  accounts: accounts.map(a => ({
    id: a.id,
    name: a.name,
    balance: a.balance,
    type: a.type
  }))
};

const accounts = await Memento.plugins.bill.getAccounts();
const defaultAccount = accounts[0];

if (!defaultAccount) {
  return { success: false, message: '没有可用账户' };
}

const bill = await Memento.plugins.bill.createBill({
  accountId: defaultAccount.id,
  type: 'expense',
  amount: 50,
  category: '餐饮',
  note: '午餐'
});

return {
  success: true,
  bill: {
    id: bill.id,
    amount: bill.amount,
    category: bill.category,
    note: bill.note
  }
};

const accounts = await Memento.plugins.bill.getAccounts();
const defaultAccount = accounts[0];

if (!defaultAccount) {
  return { success: false, message: '没有可用账户' };
}

const bill = await Memento.plugins.bill.createBill({
  accountId: defaultAccount.id,
  type: 'income',
  amount: 5000,
  category: '工资',
  note: '月工资'
});

return {
  success: true,
  bill: {
    id: bill.id,
    amount: bill.amount,
    category: bill.category
  }
};

const now = new Date();
const stats = await Memento.plugins.bill.getMonthStats({
  year: now.getFullYear(),
  month: now.getMonth() + 1
});

return {
  income: stats.income,
  expense: stats.expense,
  balance: stats.income - stats.expense,
  categoryBreakdown: stats.categoryBreakdown
};

const account = await Memento.plugins.bill.createAccount({
  name: '支付宝',
  type: 'digital',
  balance: 0
});

return {
  success: true,
  account: {
    id: account.id,
    name: account.name,
    type: account.type
  }
};
