const accounts = await Memento.plugins.bill.getAccounts();

const result = {
  count: accounts.length,
  accounts: accounts.map(a => ({
    id: a.id,
    name: a.name,
    balance: a.balance,
    type: a.type
  }))
};

setResult(result);
return result;

const accounts = await Memento.plugins.bill.getAccounts();
const defaultAccount = accounts[0];

if (!defaultAccount) {
  const result = { success: false, message: '没有可用账户' };
  setResult(result);
  return result;
}

const bill = await Memento.plugins.bill.createBill({
  accountId: defaultAccount.id,
  type: 'expense',
  amount: 50,
  category: '餐饮',
  description: '午餐',
  date: new Date().toISOString()
});

const result = {
  success: true,
  bill: {
    id: bill.id,
    amount: bill.amount,
    category: bill.category,
    type: bill.type,
    description: bill.description
  }
};

setResult(result);
return result;

const accounts = await Memento.plugins.bill.getAccounts();
const defaultAccount = accounts[0];

if (!defaultAccount) {
  const result = { success: false, message: '没有可用账户' };
  setResult(result);
  return result;
}

const bill = await Memento.plugins.bill.createBill({
  accountId: defaultAccount.id,
  type: 'income',
  amount: 5000,
  category: '工资',
  description: '月工资',
  date: new Date().toISOString()
});

const result = {
  success: true,
  bill: {
    id: bill.id,
    amount: bill.amount,
    category: bill.category,
    type: bill.type,
    description: bill.description
  }
};

setResult(result);
return result;

const currentDate = await Memento.system.getCustomDate();
const stats = await Memento.plugins.bill.getMonthStats({
  year: currentDate.year,
  month: currentDate.month
});

const result = {
  income: stats.income,
  expense: stats.expense,
  balance: stats.income - stats.expense,
  categoryBreakdown: stats.categoryBreakdown
};

setResult(result);
return result;

const account = await Memento.plugins.bill.createAccount({
  name: '支付宝',
  type: 'digital',
  balance: 0
});

const result = {
  success: true,
  account: {
    id: account.id,
    name: account.name,
    type: account.type
  }
};

setResult(result);
return result;
