const todayDate = await Memento.system.getCustomDate({format: 'yyyy-MM-dd'});
const response = await Memento.plugins.calendarAlbum.getPhotos({
  date: todayDate
});

if (response.success) {
  const result = {
    count: response.data.length,
    photos: response.data.map(p => ({
      id: p.id,
      caption: p.caption,
      tags: p.tags
    }))
  };
  setResult(result);
  return result;
} else {
  throw new Error(response.error || '获取照片失败');
}

const tags = await Memento.plugins.calendarAlbum.getTags();

const result = {
  count: tags.length,
  tags: tags
};

setResult(result);
return result;

const stats = await Memento.plugins.calendarAlbum.getStats();

const result = {
  totalPhotos: stats.totalPhotos,
  totalDays: stats.totalDays,
  tagCount: stats.tagCount
};

setResult(result);
return result;
