const todayDate = await Memento.system.getCustomDate({format: 'yyyy-MM-dd'});
const photos = await Memento.plugins.calendarAlbum.getPhotos({
  date: todayDate
});

const result = {
  count: photos.length,
  photos: photos.map(p => ({
    id: p.id,
    caption: p.caption,
    tags: p.tags
  }))
};

setResult(result);
return result;

const tags = await Memento.plugins.calendarAlbum.getTags();

const result = {
  count: tags.length,
  tags: tags
};

setResult(result);
return result;

const stats = await Memento.plugins.calendarAlbum.getStats();

const result = {
  totalPhotos: stats.totalPhotos,
  totalDays: stats.totalDays,
  tagCount: stats.tagCount
};

setResult(result);
return result;
