const days = await Memento.plugins.day.getDays();

const result = {
  count: days.length,
  days: days.map(d => ({
    id: d.id,
    name: d.name,
    date: d.date,
    type: d.type
  }))
};
setResult(result);
return result;

const upcoming = await Memento.plugins.day.getUpcoming({
  days: 30
});

const result = {
  count: upcoming.length,
  days: upcoming.map(d => ({
    name: d.name,
    date: d.date,
    daysUntil: d.daysUntil
  }))
};
setResult(result);
return result;

const day = await Memento.plugins.day.createDay({
  name: '结婚纪念日',
  date: '2020-01-01',
  type: 'anniversary',
  repeat: 'yearly'
});

const result = {
  success: true,
  day: {
    id: day.id,
    name: day.name,
    date: day.date
  }
};
setResult(result);
return result;
