const days = await Memento.plugins.day.getDays();

const result = {
  count: days.data?.length || 0,
  total: days.total || 0,
  days: days.data?.map(d => ({
    id: d.id,
    title: d.title,
    targetDate: d.targetDate,
    daysRemaining: d.daysRemaining,
    isToday: d.isToday,
    isExpired: d.isExpired
  })) || []
};
setResult(result);
return result;

const upcoming = await Memento.plugins.day.getUpcoming({
  withinDays: 30
});

const result = {
  success: upcoming.success,
  count: upcoming.count || 0,
  days: upcoming.days?.map(d => ({
    id: d.id,
    title: d.title,
    targetDate: d.targetDate,
    daysRemaining: d.daysRemaining
  })) || []
};
setResult(result);
return result;

const result = await Memento.plugins.day.createDay({
  title: '结婚纪念日',
  targetDate: '2020-01-01T00:00:00.000Z'
});

if (result.success) {
  setResult({
    success: true,
    day: {
      id: result.data.id,
      title: result.data.title,
      targetDate: result.data.targetDate
    }
  });
} else {
  setResult({
    success: false,
    error: result.error
  });
}

return result;

const dayId = params.dayId;
if (!dayId) {
  return setResult({
    error: '缺少必需参数: dayId'
  });
}

const result = await Memento.plugins.day.getDayById({
  id: dayId
});

setResult(result);
return result;
