const warehouses = await Memento.plugins.goods.getWarehouses();

const result = {
  count: warehouses.length,
  warehouses: warehouses.map(w => ({
    id: w.id,
    name: w.name,
    itemCount: w.itemCount
  }))
};
setResult(result);
return result;

const items = await Memento.plugins.goods.getItems();

const result = {
  count: items.length,
  items: items.map(i => ({
    id: i.id,
    name: i.name,
    warehouse: i.warehouseName,
    quantity: i.quantity
  }))
};
setResult(result);
return result;

const warehouses = await Memento.plugins.goods.getWarehouses();
const warehouse = warehouses[0];

if (!warehouse) {
  const errorResult = { success: false, message: '没有仓库' };
  setResult(errorResult);
  return errorResult;
}

const item = await Memento.plugins.goods.createItem({
  name: 'MacBook Pro',
  warehouseId: warehouse.id,
  quantity: 1,
  description: '2023款 14寸'
});

const result = {
  success: true,
  item: {
    id: item.id,
    name: item.name,
    warehouse: warehouse.name
  }
};
setResult(result);
return result;

const stats = await Memento.plugins.goods.getStats();

const result = {
  totalItems: stats.totalItems,
  totalWarehouses: stats.totalWarehouses,
  totalValue: stats.totalValue
};
setResult(result);
return result;
