const habits = await Memento.plugins.habits.getHabits();

const result = {
  count: habits.length,
  habits: habits.map(h => ({
    id: h.id,
    name: h.name,
    targetMinutes: h.targetMinutes,
    totalMinutes: h.totalMinutes
  }))
};
setResult(result);
return result;

const habit = await Memento.plugins.habits.createHabit({
  name: '每天阅读',
  targetMinutes: 30
});

const result = {
  success: true,
  habit: {
    id: habit.id,
    name: habit.name,
    targetMinutes: habit.targetMinutes
  }
};
setResult(result);
return result;

const habits = await Memento.plugins.habits.getHabits();
const habit = habits[0];

if (!habit) {
  const errorResult = { success: false, message: '没有习惯' };
  setResult(errorResult);
  return errorResult;
}

await Memento.plugins.habits.checkin({
  habitId: habit.id,
  minutes: 30
});

const result = {
  success: true,
  habit: {
    name: habit.name,
    minutes: 30
  }
};
setResult(result);
return result;

const stats = await Memento.plugins.habits.getStats();

const result = {
  totalHabits: stats.totalHabits,
  todayCompleted: stats.todayCompleted,
  totalMinutes: stats.totalMinutes
};
setResult(result);
return result;

const todayHabits = await Memento.plugins.habits.getTodayHabits();

const result = {
  count: todayHabits.length,
  habits: todayHabits.map(h => ({
    name: h.name,
    completed: h.completed,
    todayMinutes: h.todayMinutes,
    targetMinutes: h.targetMinutes
  }))
};
setResult(result);
return result;
