const timeInfo = await Memento.system.getCurrentTime();
const today = new Date(timeInfo.timestamp);
const weekStart = new Date(today);
weekStart.setDate(today.getDate() - today.getDay() + 1);
const weekEnd = new Date(weekStart);
weekEnd.setDate(weekStart.getDate() + 6);
const startDate = weekStart.toISOString().split('T')[0] + 'T00:00:00.000Z';
const endDate = weekEnd.toISOString().split('T')[0] + 'T23:59:59.000Z';
const events = await Memento.plugins.calendar.getEventsByDateRange({startDate: startDate, endDate: endDate});
setResult(`本周有 ${events.length} 个事件`);

const timeInfo = await Memento.system.getCurrentTime();
const startTime = new Date(timeInfo.timestamp).toISOString().replace(/T.*/, 'T09:00:00.000Z');
const event = await Memento.plugins.calendar.createEvent({title: '项目会议', description: '讨论Q1计划', startTime: startTime});
setResult(`事件已创建: ${event.id}`);

