const result = await Memento.plugins.checkin.checkin('item-id-123', '今天起床很顺利');
if (result.success) {
  setResult(`签到成功！连续签到 ${result.consecutiveDays} 天`);
} else {
  setResult(result.message);
}

const timeInfo = await Memento.system.getCurrentTime();
const year = timeInfo.year;
const month = String(timeInfo.month).padStart(2, '0');
const lastDay = new Date(year, timeInfo.month, 0).getDate();
const startDate = `${year}-${month}-01`;
const endDate = `${year}-${month}-${String(lastDay).padStart(2, '0')}`;
const history = await Memento.plugins.checkin.getCheckinHistory(startDate, endDate);
setResult(`本月共签到 ${history.length} 次`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const history = await Memento.plugins.checkin.getCheckinHistory(today, today);
setResult(JSON.stringify(history, null, 2));

const result = await Memento.plugins.checkin.createCheckinItem(
  '晨跑',
  '健康习惯',
  '每天早上6点跑步30分钟',
  59524,
  '0xFF4CAF50'
);
setResult(`创建成功，项目ID: ${result.item.id}`);
