examples.code:
const contact = await Memento.plugins.contact.getContact({contactId: 'contact123'});
if (contact.error) {
  setResult('联系人不存在');
} else {
  setResult(JSON.stringify(contact, null, 2));
}

const contact = await Memento.plugins.contact.createContact({
  name: '李四',
  phone: '13900139000',
  address: '北京市海淀区',
  notes: '重要客户',
  tags: ['朋友', '客户'],
  customFields: {公司: '北京科技', 职位: '经理'}
});
setResult(`联系人ID: ${contact.id}`);

const contact = await Memento.plugins.contact.createContact({
  id: 'contact123',
  name: '王五',
  phone: '13700137000',
  address: '上海市浦东新区',
  notes: '多年好友',
  tags: ['朋友'],
  customFields: {公司: '上海科技', 职位: '工程师'}
});
setResult(`联系人已创建，自定义ID: ${contact.id}`);

const timeInfo = await Memento.system.getCurrentTime();
const timestamp = new Date(timeInfo.timestamp).toISOString().replace(/T.*/, 'T14:00:00.000Z');
const record = await Memento.plugins.contact.addInteraction({
  contactId: 'contact123',
  notes: '三方会议讨论合作',
  dateStr: timestamp,
  participants: ['contact456', 'contact789']
});
setResult('已记录多人会议');

const contact = await Memento.plugins.contact.findContactBy({field: 'id', value: 'contact123'});
if (contact) {
  setResult(`找到联系人: ${contact.name}，电话: ${contact.phone}`);
} else {
  setResult('未找到该联系人');
}

const contact = await Memento.plugins.contact.findContactById({id: 'contact123'});
if (contact) {
  setResult(`联系人姓名: ${contact.name}, 电话: ${contact.phone}`);
} else {
  setResult('联系人不存在');
}

const record = await Memento.plugins.contact.findInteractionBy({field: 'id', value: 'record123'});
if (record) {
  setResult(`交互记录: ${record.notes}`);
} else {
  setResult('未找到该记录');
}

const record = await Memento.plugins.contact.findInteractionById({id: 'record123'});
if (record) {
  setResult(`交互内容: ${record.notes}, 日期: ${record.date}`);
} else {
  setResult('记录不存在');
}
