examples.code:
const fields = JSON.stringify([{name: '标题', type: 'Text'}, {name: '日期', type: 'Date'}]);
const db = await Memento.plugins.database.createDatabase({name: '任务库', description: '任务管理', fieldsJson: fields});
setResult(`数据库${db.id}已创建`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const fields = JSON.stringify({标题: '新任务', 日期: today, 完成: false});
const record = await Memento.plugins.database.createRecord({databaseId: 'db123', fieldsJson: fields});
setResult(`记录已创建: ${record.id}`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const fields = JSON.stringify({
  标题: '项目任务',
  日期: today,
  完成: false,
  优先级: '高',
  负责人: '张三'
});
const record = await Memento.plugins.database.createRecord({
  databaseId: 'db123',
  fieldsJson: fields,
  id: 'rec456'
});
setResult(`记录已创建，自定义ID: ${record.id}`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const fields = JSON.stringify({
  id: 'rec789',
  标题: '会议记录',
  日期: today,
  参与人数: 5,
  会议类型: '项目评审'
});
const record = await Memento.plugins.database.createRecord({databaseId: 'db123', fieldsJson: fields});
setResult(`记录已创建，ID来自fields: ${record.id}`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const updates = JSON.stringify({完成: true, 完成日期: today});
const record = await Memento.plugins.database.updateRecord({databaseId: 'db123', recordId: 'rec456', fieldsJson: updates});
setResult('记录已更新');

const filters = JSON.stringify({完成: false, 优先级: '高'});
const urgent = await Memento.plugins.database.query({databaseId: 'db123', filtersJson: filters});
setResult(`未完成的高优先级任务: ${urgent.length} 条`);
