const timeInfo = await Memento.system.getCurrentTime();
const nextYear = timeInfo.year + 1;
const newYearDate = `${nextYear}-01-01`;
const day = await Memento.plugins.day.createMemorialDay('新年', newYearDate, 'countdown', 7);
setResult(`纪念日已创建: ${day.title}`);

const timeInfo = await Memento.system.getCurrentTime();
const nextYear = timeInfo.year + 1;
const newYearDate = `${nextYear}-01-01`;
const days = await Memento.plugins.day.getDaysUntil(newYearDate);
setResult(`距离${nextYear}年新年还有 ${days} 天`);
