const timeInfo = await Memento.system.getCurrentTime();
const nextYear = timeInfo.year + 1;
const newYearDate = `${nextYear}-01-01`;
const result = await Memento.plugins.day.createMemorialDay({name: '新年', date: newYearDate});
setResult(result.success ? `纪念日已创建: ${result.data.title}` : result.error);

const result = await Memento.plugins.day.createMemorialDay({
  name: '生日',
  date: '2025-06-15',
  notesJson: JSON.stringify(['准备礼物', '订餐厅'])
});
setResult(result.success ? '创建成功' : result.error);

const timeInfo = await Memento.system.getCurrentTime();
const nextYear = timeInfo.year + 1;
const newYearDate = `${nextYear}-01-01`;
const result = await Memento.plugins.day.getDaysUntil({date: newYearDate});
if (result.success) {
  setResult(`距离${nextYear}年新年还有 ${result.days} 天`);
} else {
  setResult(result.error);
}

const result = await Memento.plugins.day.getUpcomingDays({withinDays: 7});
if (result.success) {
  setResult(`未来一周有 ${result.count} 个纪念日`);
} else {
  setResult(result.error);
}
