const itemData = JSON.stringify({title: '笔记本电脑'});
const item = await Memento.plugins.goods.createGoodsItem({warehouseId: 'wh123', itemData});
setResult(`物品已创建: ${item.title}`);

const itemData = JSON.stringify({
  title: '笔记本',
  customFields: [{key: '品牌', value: 'Apple'}, {key: '型号', value: 'MacBook Pro'}]
});
const item = await Memento.plugins.goods.createGoodsItem({warehouseId: 'wh123', itemData});
setResult(`物品ID: ${item.id}`);

const itemData = JSON.stringify({title: '新标题'});
const updated = await Memento.plugins.goods.updateGoodsItem({itemId: 'item456', itemData});
setResult(`标题已更新为: ${updated.title}`);

const timeInfo = await Memento.system.getCurrentTime();
const today = `${timeInfo.year}-${String(timeInfo.month).padStart(2, '0')}-${String(timeInfo.day).padStart(2, '0')}`;
const item = await Memento.plugins.goods.addUsageRecord({itemId: 'item456', dateStr: today, note: '用于编程'});
setResult('已记录使用');
