examples.code:
const habit = await Memento.plugins.habits.createHabit({title: '学习React', durationMinutes: 90, skillId: 'react_development'});
setResult(`习惯已创建: ${habit.title}，自动关联技能: ${habit.skillId}`);
// 技能'react_development'会自动创建，标题也是'react_development'

const status = await Memento.plugins.habits.getTimerStatus({habitId: 'habit123'});
if (status.isRunning) {
  setResult(`正在计时: ${status.elapsedSeconds} 秒`);
} else {
  setResult('无正在进行的计时');
}

const habit = await Memento.plugins.habits.findHabitBy({field: 'id', value: 'habit123'});
if (habit) {
  setResult(`找到习惯: ${habit.title}，目标时长: ${habit.durationMinutes}分钟`);
} else {
  setResult('未找到该习惯');
}

const habits = await Memento.plugins.habits.findHabitByTitle({title: '学习', fuzzy: true, findAll: true});
const totalMinutes = habits.reduce((sum, h) => sum + (h.durationMinutes || 0), 0);
setResult(`找到 ${habits.length} 个学习相关习惯，总目标时长: ${totalMinutes}分钟`);

const skill = await Memento.plugins.habits.findSkillBy({field: 'id', value: 'skill123'});
if (skill) {
  setResult(`找到技能: ${skill.title}，已累积: ${skill.totalMinutes}分钟`);
} else {
  setResult('未找到该技能');
}

const skills = await Memento.plugins.habits.findSkillBy({field: 'group', value: '学习', findAll: true});
const totalHours = skills.reduce((sum, s) => sum + (s.totalMinutes || 0), 0) / 60;
setResult(`学习分组有 ${skills.length} 个技能，总累积: ${totalHours.toFixed(1)}小时`);

const record = await Memento.plugins.habits.findCompletionRecordBy({field: 'id', value: 'record123'});
if (record) {
  setResult(`记录时长: ${record.durationMinutes}分钟, 备注: ${record.note || '无'}`);
} else {
  setResult('未找到该记录');
}

const records = await Memento.plugins.habits.findCompletionRecordBy({field: 'habitId', value: 'habit123', findAll: true});
const totalMinutes = records.reduce((sum, r) => sum + (r.durationMinutes || 0), 0);
setResult(`该习惯共打卡 ${records.length} 次，累积 ${totalMinutes}分钟`);

const record = await Memento.plugins.habits.findCompletionRecordById({id: 'record123'});
if (record) {
  setResult(`完成时间: ${record.completedAt}, 时长: ${record.durationMinutes}分钟`);
} else {
  setResult('记录不存在');
}
