const timeInfo = await Memento.system.getCurrentTime();
const today = new Date(timeInfo.timestamp);
const weekStart = new Date(today);
weekStart.setDate(today.getDate() - today.getDay() + 1);
const weekEnd = new Date(weekStart);
weekEnd.setDate(weekStart.getDate() + 6);
const startTime = weekStart.toISOString().split('.')[0] + 'Z';
const endTime = weekEnd.toISOString().replace(/T.*/, 'T23:59:59Z');
const results3 = await Memento.plugins.notes.searchNotes({ keyword: '', startDate: startTime, endDate: endTime });
setResult(results3);

const timeInfo = await Memento.system.getCurrentTime();
const year = timeInfo.year;
const month = String(timeInfo.month).padStart(2, '0');
const lastDay = new Date(year, timeInfo.month, 0).getDate();
const startTime = `${year}-${month}-01T00:00:00Z`;
const endTime = `${year}-${month}-${String(lastDay).padStart(2, '0')}T23:59:59Z`;
const results4 = await Memento.plugins.notes.searchNotes({ keyword: '会议', tags: ['工作', '产品'], startDate: startTime, endDate: endTime });
setResult(results4);
