const time = await Memento.system.getCurrentTime();
const msg = `今天是${time.year}年${time.month}月${time.day}日，星期${time.weekdayName}`;
setResult(msg);

const info = await Memento.system.getDeviceInfo();
const msg = `您正在使用 ${info.platform} 设备`;
setResult(msg);

const app = await Memento.system.getAppInfo();
const msg = `${app.appName} v${app.version}`;
setResult(msg);
