examples.code:
// 获取所有目标
const allGoals = await Memento.plugins.tracker.getGoals({});
setResult(allGoals);

// 获取进行中的目标
const activeGoals = await Memento.plugins.tracker.getGoals({ status: 'active' });
setResult(activeGoals);

// 获取'学习'分组的目标
const studyGoals = await Memento.plugins.tracker.getGoals({ group: '学习' });
setResult(studyGoals);

// 获取'健康'分组中已完成的目标
const completedHealthGoals = await Memento.plugins.tracker.getGoals({ status: 'completed', group: '健康' });
setResult(completedHealthGoals);

const goal = await Memento.plugins.tracker.getGoal({ goalId: '1736950800000' });
const goalData = JSON.parse(goal);
const result = `目标名称: ${goalData.name}, 进度: ${goalData.currentValue}/${goalData.targetValue}`;
setResult(result);

// 创建每日阅读目标
const goal = await Memento.plugins.tracker.createGoal({
  name: '每日阅读',
  unitType: '分钟',
  targetValue: 30,
  group: '学习'
});
setResult(goal);

// 创建每周运动目标
const weeklyGoal = await Memento.plugins.tracker.createGoal({
  name: '每周运动',
  unitType: '小时',
  targetValue: 5,
  group: '健康',
  dateType: 'weekly'
});
setResult(weeklyGoal);

// 更新目标名称和目标值
const updated = await Memento.plugins.tracker.updateGoal({
  goalId: '1736950800000',
  updateJson: {
    name: '每日深度阅读',
    targetValue: 60
  }
});
setResult(updated);

// 更新提醒时间和分组
const updated2 = await Memento.plugins.tracker.updateGoal({
  goalId: '1736950800000',
  updateJson: {
    reminderTime: '20:00',
    group: '个人成长'
  }
});
setResult(updated2);

// 添加今日阅读记录
const record = await Memento.plugins.tracker.recordData({
  goalId: '1736950800000',
  value: 30,
  note: '阅读《代码整洁之道》'
});
setResult(record);

// 添加昨日的记录
const timeInfo = await Memento.system.getCurrentTime();
const yesterday = new Date(timeInfo.timestamp);
yesterday.setDate(yesterday.getDate() - 1);
const pastTime = yesterday.toISOString().replace(/T.*/, 'T20:00:00.000Z');
const pastRecord = await Memento.plugins.tracker.recordData({
  goalId: '1736950800000',
  value: 45,
  note: '昨日补记',
  dateTime: pastTime
});
setResult(pastRecord);

// 获取所有记录
const allRecords = await Memento.plugins.tracker.getRecords({ goalId: '1736950800000' });
setResult(allRecords);

// 获取最近 10 条记录
const recentRecords = await Memento.plugins.tracker.getRecords({ goalId: '1736950800000', limit: 10 });
const records = JSON.parse(recentRecords);
const result = records.map(record => `${record.recordedAt}: ${record.value} ${record.note || ''}`).join('\n');
setResult(result);

const progress = await Memento.plugins.tracker.getProgress({ goalId: '1736950800000' });
const progressData = JSON.parse(progress);
const result = `进度: ${progressData.percentage}%\n完成状态: ${progressData.isCompleted ? '已完成' : '进行中'}`;
setResult(result);

// 获取全局统计
const stats = await Memento.plugins.tracker.getStats({});
const statsData = JSON.parse(stats);
const result = `总目标数: ${statsData.totalGoals}\n今日完成: ${statsData.todayCompleted}\n本月完成: ${statsData.monthCompleted}\n整体进度: ${(statsData.overallProgress * 100).toFixed(1)}%\n分组列表: ${statsData.groups.join(', ')}`;
setResult(result);

// 获取单个目标统计
const goalStats = await Memento.plugins.tracker.getStats({ goalId: '1736950800000' });
const goalStatsData = JSON.parse(goalStats);
const result = `目标名称: ${goalStatsData.goalName}\n总记录数: ${goalStatsData.totalRecords}\n累计值: ${goalStatsData.totalValue}`;
setResult(result);
