examples.code:
// 获取所有签到项目
var items = await Memento.checkin.getCheckinItems();
console.log('签到项目:', items);
return items;

// 创建一个新的签到项目
// 参数: name, group(可选), description(可选)
var item = await Memento.checkin.createCheckinItem(
  '早起',          // 名称
  '健康习惯',      // 分组(可选)
  '每天6点起床'    // 描述(可选)
);

console.log('创建成功:', item);
return item;

// 执行签到操作
var items = await Memento.checkin.getCheckinItems();

if (items.length > 0) {
  var itemId = items[0].id;

  // 参数: itemId, note(可选)
  var result = await Memento.checkin.checkin(
    itemId,
    '今天状态很好!'  // 备注(可选)
  );

  console.log('签到结果:', result);
  return result;
} else {
  return '请先创建签到项目';
}

// 获取签到历史记录
var items = await Memento.checkin.getCheckinItems();

if (items.length > 0) {
  var itemId = items[0].id;

  // 参数: itemId, startDate(可选), endDate(可选)
  var history = await Memento.checkin.getCheckinHistory(
    itemId,
    '2025-11-01',  // 开始日期(可选)
    '2025-11-14'   // 结束日期(可选)
  );

  console.log('历史记录:', history);
  return history;
} else {
  return '没有签到项目';
}

// 获取全局统计或单项统计
// 参数: itemId(可选)
var stats = await Memento.checkin.getStats();
console.log('全局统计:', stats);
// 包含: totalItems, todayCheckins, totalCheckins, completionRate
return stats;
