examples.code:
// 获取所有联系人列表
var contacts = await Memento.contact.getContacts();
console.log('联系人列表:', contacts);
return contacts;

// 创建一个新联系人
// 参数: name, phone, avatar, address, notes, tags, customFields
var contact = await Memento.contact.createContact(
  '张三',                    // 姓名
  '13800138000',            // 电话
  null,                     // 头像(可选)
  '北京市海淀区',           // 地址(可选)
  '重要客户',               // 备注(可选)
  '["朋友", "同事"]',       // 标签(可选)
  '{"公司": "科技公司"}'   // 自定义字段(可选)
);

console.log('创建成功:', contact);
return contact;

// 为联系人添加交互记录
var contacts = await Memento.contact.getContacts();

if (contacts.length > 0) {
  var contactId = contacts[0].id;

  // 参数: contactId, notes, date(可选), participants(可选)
  var interaction = await Memento.contact.addInteraction(
    contactId,
    '今天一起讨论了项目进展',  // 内容
    null,                       // 日期(可选)
    null                        // 参与者(可选)
  );

  console.log('记录成功:', interaction);
  return interaction;
} else {
  return '请先创建联系人';
}

// 获取联系人的交互历史
var contacts = await Memento.contact.getContacts();

if (contacts.length > 0) {
  var contactId = contacts[0].id;

  var interactions = await Memento.contact.getInteractions(contactId);
  console.log('交互记录:', interactions);
  return interactions;
} else {
  return '没有联系人';
}

// 获取所有已使用的标签
var tags = await Memento.contact.getAllTags();
console.log('标签列表:', tags);
return tags;
