examples.code:
// 获取所有笔记
// 参数: folderId(可选)
var notes = await Memento.notes.getNotes();
console.log('笔记列表:', notes);
return notes;

// 创建一篇笔记
// 参数: title, content, folderId(可选), tags(可选)
var note = await Memento.notes.createNote(
  '项目笔记',                        // 标题
  '# 项目概述\n\n这是一个新项目...', // 内容(Markdown)
  'root',                           // 文件夹ID(可选)
  '["工作", "项目"]'                // 标签(可选)
);

console.log('创建成功:', note);
return note;

// 搜索笔记
// 参数: keyword, tags(可选), startDate(可选), endDate(可选)
var results = await Memento.notes.searchNotes(
  '项目',           // 关键词
  '["工作"]',       // 标签(可选)
  null,             // 开始日期(可选)
  null              // 结束日期(可选)
);

console.log('搜索结果:', results);
return results;

// 获取所有文件夹
var folders = await Memento.notes.getFolders();
console.log('文件夹列表:', folders);
return folders;

// 移动笔记到其他文件夹
var notes = await Memento.notes.getNotes();
var folders = await Memento.notes.getFolders();

if (notes.length > 0 && folders.length > 0) {
  var noteId = notes[0].id;
  var targetFolderId = folders[0].id;

  // 参数: noteId, targetFolderId
  var result = await Memento.notes.moveNote(noteId, targetFolderId);
  console.log('移动成功:', result);
  return result;
} else {
  return '需要笔记和文件夹';
}
