examples.code:
// 获取当前积分余额
var points = await Memento.store.getPoints();
console.log('当前积分:', points);
return points;

// 获取所有商品
var products = await Memento.store.getProducts();
console.log('商品列表:', products);
return products;

// 兑换商品
var products = await Memento.store.getProducts();

if (products.length > 0) {
  var productId = products[0].id;

  // 参数: productId
  var result = await Memento.store.redeem(productId);

  if (result.success) {
    console.log('兑换成功！剩余积分:', result.currentPoints);
  } else {
    console.log('兑换失败:', result.message);
  }

  return result;
} else {
  return '没有可用商品';
}

// 增加或减少积分
// 参数: points(正数增加, 负数减少), reason
var result = await Memento.store.addPoints(
  100,              // 积分数量
  '完成每日任务'    // 原因
);

console.log('操作成功:', result);
return result;

// 获取兑换历史记录
var history = await Memento.store.getRedeemHistory();
console.log('兑换历史:', history);
return history;

// 获取积分变动历史
var history = await Memento.store.getPointsHistory();
console.log('积分历史:', history);
return history;
