examples.code:
// 获取所有目标
// 参数: status(可选), group(可选)
var goals = await Memento.tracker.getGoals();
console.log('目标列表:', goals);
return goals;

// 创建一个目标
// 参数: name, unitType, targetValue, group, icon, dateType
var goal = await Memento.tracker.createGoal(
  '每日阅读',     // 名称
  '分钟',         // 单位类型
  30,             // 目标值
  '学习',         // 分组(可选)
  null,           // 图标(可选)
  null            // 日期类型(可选)
);

console.log('创建成功:', goal);
return goal;

// 为目标记录数据
var goals = await Memento.tracker.getGoals();

if (goals.length > 0) {
  var goalId = goals[0].id;

  // 参数: goalId, value, note(可选), dateTime(可选)
  var record = await Memento.tracker.recordData(
    goalId,
    30,                  // 完成值
    '阅读技术文档'       // 备注(可选)
  );

  console.log('记录成功:', record);
  return record;
} else {
  return '请先创建目标';
}

// 获取目标完成进度
var goals = await Memento.tracker.getGoals();

if (goals.length > 0) {
  var goalId = goals[0].id;

  var progress = await Memento.tracker.getProgress(goalId);
  console.log('进度信息:', progress);
  // 包含: currentValue, targetValue, percentage
  return progress;
} else {
  return '没有目标';
}

// 获取全局统计
var stats = await Memento.tracker.getStats();
console.log('全局统计:', stats);
// 包含: totalGoals, todayCompleted, monthCompleted 等
return stats;
