const todayDate = await Memento.system.getCustomDate({format: 'yyyy-MM-dd'});
const photo = await Memento.plugins.calendarAlbum.createPhoto({
  caption: '美好瞬间',
  imageUrl: 'file:///path/to/photo.jpg',
  date: todayDate,
  tags: ['生活', '记录']
});

const result = {
  success: true,
  photo: {
    id: photo.id,
    caption: photo.title,
    date: photo.createdAt
  }
};

setResult(result);
return result;
